package leetcode;

import java.util.*;
/**
 * 문제 : https://leetcode.com/problems/find-all-anagrams-in-a-string/
 * 레벨 : Medium
 * 결과 : 해결(runtime 777ms faster than 11.26%, memory 117.7MB less than 5.02%)
 */
public class FindAllAnagramsinaString {

    public static List<Integer> findAnagrams(String s, String p) {

        int length = p.length();
        List<Integer> answer = new ArrayList<>();
        char[] alphabet_p = new char[26];

        for(int i = 0; i < p.length(); i++){
            alphabet_p[p.charAt(i) - 'a']++;
        }

        for (int i = 0; i < s.length() - length+1; i++) {
            char[] alphabet_s = new char[26];

            String temp = s.substring(i, i + length);
            for(int j = 0; j < temp.length(); j++) {
                alphabet_s[temp.charAt(j) - 'a']++;
            }

            boolean isSame = true;
            for(int k = 0; k < 26; k++){
                if (alphabet_p[k]!=alphabet_s[k]) {
                    isSame = false;
                    break;
                }
            }

            if(isSame == true){
                answer.add(i);
            }
        }

        return answer;
    }


    public static void main(String[] args) {
        String s = "";
        String p = "";
        System.out.println(findAnagrams(s, p));

    }
}
